mapper.select:
SELECT u_id, u_name, u_roll
FROM user
WHERE u_id = #{u_id }
	AND u_pwd = #{u_pwd }

mapper.update:
UPDATE board
	SET b_title = #{b_title}
	   ,b_comment = #{b_comment}
	   ,b_topYN = #{b_topYN}
	   ,b_modifier = #{b_modifier}
	   ,b_modifydate = sysdate()
WHERE b_seq = #{b_seq}

mapper.select:
SELECT b.b_seq, b_title, b_comment, b_regdate, b_writer, b_topYN, b_modifier, b_modifydate
FROM board b, showlist s 
WHERE b.b_seq = s.b_seq AND s.u_id = #{u_id}
LIMIT #{firstIndex}, #{recordCountPerPage}

SELECT COUNT(*)
FROM board b, showlist s 
WHERE b.b_seq = s.b_seq AND s.u_id = #{u_id}

SELECT c_seq, c_name
FROM checklist
WHERE b_seq = #{b_seq}

SELECT u_id
FROM showlist
WHERE b_seq = #{b_seq}

SELECT a_checked, a_regdate, a_modifier, a_modifydate, u_id, c.c_seq
FROM answer a, checklist c
WHERE c.c_seq = a.c_seq AND c.b_seq = #{b_seq} AND a.u_id = #{u_id}

SELECT a_checked, a_regdate, u_id, c_seq
FROM answer
WHERE u_id = #{u_id}
	AND c_seq = #{c_seq}

mapper.update:
update answer
	SET a_checked = #{a_checked}
	   ,a_modifier = #{u_id}
	   ,a_modifydate = sysdate()
	WHERE u_id = #{answer_id}
	AND c_seq = #{c_seq}

mapper.select:
SELECT *
FROM log
WHERE u_id = #{u_id}
	AND b_seq = #{b_seq}

mapper.update:
UPDATE log
	SET l_readdate = sysdate()
	WHERE u_id = #{u_id} AND b_seq = #{b_seq}
